<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Cms extends CI_Controller {    public function __construct() {        parent::__construct();        $this->load->database();        $this->load->model('Cms_Model');        $this->load->model('Home_Model');        $this->is_admin_logged_in();    }    private function is_admin_logged_in() {        if (!$this->session->userdata('admin_logged_in')) {            redirect('login/index');        }    }    public function index() {        redirect('home/dashboard');    }    public function pages($id) {        if($id != ''){            if($_POST)            {                $content = $this->input->post('content');                $page_id = $this->input->post('page_id');                if($content != '')                {                    $this->db->query($sql = "update cms set content='".mysql_real_escape_string($content)."' where id='".$page_id."'");                    redirect('cms/pages/'.$page_id);                }            }            else            {                $data['result'] = $this->Cms_Model->getcms($id);                $this->load->view('cms/index', $data);            }        }    }}/* End of file welcome.php *//* Location: ./application/controllers/welcome.php */