nction ( data ) {                            $('.loding').show();                        },                        success: function ( data ) {                            $('.loding').hide();                            alert( 'Markup Updated' );                        }                    });            }                        function updateAllAgentHotelMarkup(id){                var site_url = '<?php echo site_url(); ?>';                inputs = $('#'+id).serialize();                alert(inputs);                $.ajax({                        url: site_url+'/b2b/updateAgentHotelMarkup',                        data: inputs,                        processData: false,                        type: 'POST',                        success: function ( data ) {                            $('.loding').show();                        },                        success: function ( data ) {                            $('.loding').hide();                            alert( 'Markup Updated' );                        }                    });            }        </script>    </body></html>