);		$markup_type = 'specific_city';		$service_type = 1;				$country = 'city';		$city = $('select[name="hotel_spec_city"] option:selected').val();				var dataString = "service_type="+ $service_type +"&markup_type="+ $markup_type +"&api_name="+ $api_name +"&markup="+ $markup +"&country="+ $country +"&city="+ $city +"&markup_process="+ $markup_process +"&currency="+ $currency;				if(confirm('Are you sure you want to Add/Update B2C Specific City Hotel MarkUp?')) {			 			 $.ajax({				url: '<?php echo site_url();?>/b2c/update_b2c_markups',				type: "POST",				data: dataString,								success: function (data) {					//alert(data);					window.location = '<?php echo site_url();?>/b2c/hotel_markup_manager';				}							});		}				});			$( ".hotel_spec_hotelcity" ).change(function() {						$city_id = $(this).val();								$.ajax({				url: '<?php echo site_url();?>/b2b/get_hotel_list',				type: "POST",				data: "city_id="+ $city_id,								success: function (data) {																	   $('.hotel_spec_hotelwise').html(data);									}			});			});		// Ajax call for specific city hotel markups		$("#hotel_specific_hotelwise").submit(function(ev){		ev.preventDefault();    						$markup_process = $( "select[name='hotel_spec_hotelwise_markup_process'] option:selected" ).val();		if($markup_process == 'percentage')		{			$markup = $( "input[name='hotel_spec_hotelwise_markup']" ).val();									$currency = 'N/A';		}		else		{			$markup = $( "input[name='hotel_spec_hotelwise_markup_amt']" ).val();				$currency = $( "select[name='hotel_spec_hotelwise_currency'] option:selected" ).val();		}				if($markup == '')		{			return false;		}							$api_name = $('select[name="hotel_spec_hotel_api"] option:selected').val();		$markup_type = 'specific_hotel';		$service_type = 1;				$country = 'hotel';		$city = '